import React,{useState} from "react";
import "./User.css"
import UserDisplay from "./UserDisplay";

const users=new Array({
   name:'mahadeva',
   age:10000
});

const User=()=>{
   const [userName,setuserName]=useState('')
   const [userAge,setUserAge]=useState('');    
   const [users,setusers]=useState('');

   const ButtonHandler=(evt)=>{
      evt.preventDefault();
      console.log(evt)
      console.log('hello from button')
      

   }
  
   
  
   const SubmitHandler=(evt)=>{
      console.log(evt)
      evt.preventDefault();
      const arr1={name:userName,age:userAge}
      setusers((prev)=>{
         return [...prev,arr1]
   })
   console.log(users);
      users.push(arr1);
      console.log(users);
      console.log("hello from form");
      setUserAge('');
      
      setuserName('');
   }

   const userNameChange=(evt)=>{
      
      setuserName(evt.target.value);
      evt.preventDefault();
     
     
      
   }
   

   const userAgeChange=(evt)=>{
      
      setUserAge(evt.target.value);
      evt.preventDefault();
   }


   return (
   <div className="Wrapper" >
    <form className="form-control" onSubmit={SubmitHandler} style={{backgroundColor:'white'}}>
     <div className="UserName">
      <label htmlFor="userName"> UserName </label>
      <input type="text" id="userName" value={userName} onInput={userNameChange} placeholder="enter the username "></input>
      </div>
      <div className="Age">
      <label htmlFor="Age">Age(Years)</label>
      <input type="number" id="Age"  placeholder="Enter the Age in years" value={userAge} onInput={userAgeChange}></input>
      </div>
      <div>
      <button type="submit" onSubmit={ButtonHandler}>Add User</button>
      </div>
    </form>
    <UserDisplay></UserDisplay>
   </div>

   )


}

export default User;
